import React from 'react';
import 'bulma/css/bulma.css';
import { Link, Redirect } from 'react-router-dom';
class CreateNews extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            isAuth : false
        }
    }

    closeHandler = () => {
        if (localStorage.getItem("token")!='')
            this.setState({isAuth : true})
        else
            this.setState({isAuth : false})
    }
    render(){
        var label = {
            fontWeight: 'bold'
        }
        var buttonAttachmentStyle = {
            marginTop: '15px'
        }
        if (this.state.isAuth){
            return <Redirect to="/home"/>
        }
        return (
            <div class="modal is-active">
                <div class="modal-background"></div>
                <div class="modal-card">
                    <header class="modal-card-head">
                        <p class="modal-card-title">Create News</p>
                        <button class="delete is-large" aria-label="close" onClick={this.closeHandler}></button>
                    </header>
                    <section class="modal-card-body">
                        <textarea class="textarea"  placeholder="Ada berita baru ?"></textarea>
                        <button style={buttonAttachmentStyle} class="button is-primary">
                            Lampirkan Gambar
                        </button>
                    </section>
                </div>
            </div>
        );
    }
}

export default CreateNews
